CREATE TABLE part (  p_partkey  INT8,  p_name  VARCHAR,  p_mfgr  VARCHAR,  p_brand  VARCHAR,  p_type  VARCHAR,  p_size  INT8,  p_container VARCHAR,  p_retailprice NUMERIC,  p_comment VARCHAR,  PRIMARY KEY (p_partkey) );  CREATE TABLE supplier (  s_suppkey INT8,  s_name  VARCHAR,  s_address VARCHAR,  s_nationkey INT8,  s_phone  VARCHAR,  s_acctbal NUMERIC,  s_comment VARCHAR,  PRIMARY KEY (s_suppkey) );  CREATE TABLE partsupp (  ps_partkey INT8,  ps_suppkey INT8,  ps_availqty INT8,  ps_supplycost NUMERIC,  ps_comment VARCHAR,  PRIMARY KEY (ps_partkey, ps_suppkey) );  CREATE TABLE customer (  c_custkey INT8,  c_name  VARCHAR,  c_address VARCHAR,  c_nationkey INT8,  c_phone  VARCHAR,  c_acctbal NUMERIC,  c_mktsegment VARCHAR,  c_comment VARCHAR,  PRIMARY KEY (c_custkey) );  CREATE TABLE orders (  o_orderkey INT8,  o_custkey INT8,  o_orderstatus VARCHAR,  o_totalprice NUMERIC,  o_orderdate DATE,  o_orderpriority VARCHAR,  o_clerk  VARCHAR,  o_shippriority INT8,  o_comment VARCHAR,  PRIMARY KEY (o_orderkey) );  CREATE TABLE lineitem (  l_orderkey INT8,  l_partkey INT8,  l_suppkey INT8,  l_linenumber INT8,  l_quantity NUMERIC,  l_extendedprice NUMERIC,  l_discount NUMERIC,  l_tax  NUMERIC,  l_returnflag VARCHAR,  l_linestatus VARCHAR,  l_shipdate DATE,  l_commitdate DATE,  l_receiptdate DATE,  l_shipinstruct VARCHAR,  l_shipmode VARCHAR,  l_comment VARCHAR,  PRIMARY KEY (l_orderkey, l_linenumber) );  CREATE TABLE nation (  n_nationkey INT8,  n_name  VARCHAR,  n_regionkey INT8,  n_comment VARCHAR,  PRIMARY KEY (n_nationkey) );  CREATE TABLE region (  r_regionkey INT8,  r_name  VARCHAR,  r_comment VARCHAR,  PRIMARY KEY (r_regionkey) );
